import styled from 'styled-components';
export const DetailWrap = styled.div`
        overflow: hidden;
        width: 960px;
        margin: 0 auto;
        padding-bottom: 100px;
    }
`;

export const Header = styled.div`
    margin: 50px 0 20px 0;
    line-height: 44px;
    font-size: 34px;
    color: #333;
    font-weight: bold;
`;
