import React, { Component} from 'react';
import {connect } from 'react-redux';
import {
    HeaderWrapper,Logo,Nav,NavItem,NavSearch,Addition,Button,SearchWrap
} from './style.js';
import { CSSTransition } from 'react-transition-group';

class Header extends Component {

    constructor(props){
        super(props);
        this.handleInputFocus = this.handleInputFocus.bind(this);
        this.handleInputBlur = this.handleInputBlur.bind(this);
    }

    render(){
        return (
            <HeaderWrapper>
                <Logo href='/'/>
                <Nav>
                <NavItem className='left active'>Index</NavItem>
                <NavItem className='left'>Download</NavItem>
                <NavItem className='right'>
                        <i className="iconfont">&#xe602;</i>
                    </NavItem>
                <NavItem className='right'>Login</NavItem>
                <SearchWrap>
                <CSSTransition in={this.state.focused} timeout={200} classNames="slide">
                <NavSearch className={this.state.focused ? 'focused': ' '} 
                onFocus={this.handleInputFocus} onBlur={this.handleInputBlur}></NavSearch>
                </CSSTransition>
                <i className={this.state.focused ? 'focused iconfont': ' iconfont'}>&#xe66b;</i>
                </SearchWrap>
                </Nav>
                <Addition>
                    <Button className='reg'>Reigster</Button>              
                    <Button className='write'>
                    <i className="iconfont">&#xe63d;</i>
                        Write
                    </Button>            
                </Addition>
            </HeaderWrapper>
            );
    }

    handleInputFocus(){
        this.setState({
            focused: true
        })
    }

    handleInputBlur(){
        this.setState({
            focused: false
        })
    }
}

const mapStateToProps = (state) => {
    return{

    }
}

const mapDispathToProps = (dispatch) => {
    return{
        
    }
}
export default connect(mapStateToProps, mapDispathToProps) (Header);
