import React from 'react';
import { connect } from 'react-redux';
import {
    HeaderWrapper, Logo, Nav, NavItem, NavSearch, Addition, Button, SearchWrap,
    SearchInfo,SearchInfoSwich,SearchInfoTitle,SearchInfoItem,SearchInfoList
} from './style.js';
import { CSSTransition } from 'react-transition-group';
import  * as actionCreators from './store/actionCreator';


const Header = (props) => {
    return (
        <HeaderWrapper>
            <Logo href='/' />
            <Nav>
                <NavItem className='left active'>Index</NavItem>
                <NavItem className='left'>Download</NavItem>
                <NavItem className='right'>
                    <i className="iconfont">&#xe602;</i>
                </NavItem>
                <NavItem className='right'>Login</NavItem>
                <SearchWrap>
                    <CSSTransition in={props.focused} timeout={200} classNames="slide">
                        <NavSearch className={props.focused ? 'focused' : ' '}
                            onFocus={props.handleInputFocus} onBlur={props.handleInputBlur}></NavSearch>
                    </CSSTransition>
                    <i className={props.focused ? 'focused iconfont' : ' iconfont'}>&#xe66b;</i>
                    <SearchInfo>
                    <SearchInfoTitle>
                        Hot Search!
                        <SearchInfoSwich>
                            Switch!
                        </SearchInfoSwich>
                    </SearchInfoTitle>
                    <SearchInfoList>
                        <SearchInfoItem>Education</SearchInfoItem>
                        <SearchInfoItem>Kids</SearchInfoItem>
                        <SearchInfoItem>Pets</SearchInfoItem>
                        <SearchInfoItem>Vote</SearchInfoItem>
                        <SearchInfoItem>Invest</SearchInfoItem>
                        <SearchInfoItem>Sports</SearchInfoItem>
                        <SearchInfoItem>Vote</SearchInfoItem>
                    </SearchInfoList>
                    </SearchInfo>
                </SearchWrap>
            </Nav>
            <Addition>
                <Button className='reg'>Reigster</Button>
                <Button className='write'>
                    <i className="iconfont">&#xe63d;</i>
                    Write
                </Button>
            </Addition>
        </HeaderWrapper>
    );
}

const mapStateToProps = (state) => {
    return {
        focused: state.get('header').get('focused')
    }
}

const mapDispathToProps = (dispatch) => {
    return {
        handleInputFocus() {
            dispatch(actionCreators.searchFocus());
        },
        handleInputBlur() {
            dispatch(actionCreators.searchBlur());
        }
    }
}
export default connect(mapStateToProps, mapDispathToProps)(Header);
