import React, { Component } from 'react';
import { connect } from 'react-redux';
import {
    HeaderWrapper, Logo, Nav, NavItem, NavSearch, Addition, Button, SearchWrap,
    SearchInfo, SearchInfoSwich, SearchInfoTitle, SearchInfoItem, SearchInfoList
} from './style.js';
import { CSSTransition } from 'react-transition-group';
import * as actionCreators from './store/actionCreator';

class Header extends Component {
    render() {
        const {focused, list,handleInputFocus,handleInputBlur} = this.props;
        return (
            <HeaderWrapper>
                <Logo href='/' />
                <Nav>
                    <NavItem className='left active'>Index</NavItem>
                    <NavItem className='left'>Download</NavItem>
                    <NavItem className='right'>
                        <i className="iconfont">&#xe602;</i>
                    </NavItem>
                    <NavItem className='right'>Login</NavItem>
                    <SearchWrap>
                        <CSSTransition in={focused} timeout={200} classNames="slide">
                            <NavSearch className={focused ? 'focused' : ' '}
                                onFocus={handleInputFocus} onBlur={handleInputBlur}></NavSearch>
                        </CSSTransition>
                        <i className={focused ? 'focused iconfont' : ' iconfont'}>&#xe66b;</i>
                        {this.getListArea(focused)}
                    </SearchWrap>
                </Nav>
                <Addition>
                    <Button className='reg'>Reigster</Button>
                    <Button className='write'>
                        <i className="iconfont">&#xe63d;</i>
                    Write
                </Button>
                </Addition>
            </HeaderWrapper>
        );
    }

    getListArea(show) {
        const {focused, list, page} = this.props;
        const jsList = list.toJS();
        let pageList = [];
        for (let i = (page - 1) * 10; i <  page * 10; i++){
            pageList.push(<SearchInfoItem key={jsList[i]}>{jsList[i]}</SearchInfoItem>);
        }
        if (focused) {
            return (
                <SearchInfo>
                    <SearchInfoTitle>
                        Hot Search!
                    <SearchInfoSwich>
                            Switch!
                    </SearchInfoSwich>
                    </SearchInfoTitle>
                    <SearchInfoList>
                        {   pageList     }
                    </SearchInfoList>
                </SearchInfo>
            );
        } else {
            return null;
        }
    }
}

const mapStateToProps = (state) => {
    return {
        focused: state.get('header').get('focused'),
        list: state.getIn(['header', 'list']),
        page: state.get('header').get('page')
    }
}

const mapDispathToProps = (dispatch) => {
    return {
        handleInputFocus() {
            dispatch(actionCreators.getList());
            dispatch(actionCreators.searchFocus());
        },
        handleInputBlur() {
            dispatch(actionCreators.searchBlur());
        }
    }
}
export default connect(mapStateToProps, mapDispathToProps)(Header);
